import {
  AbstractControl,
  FormArray,
  FormControl,
  FormGroup,
} from '@angular/forms';
import * as _ from 'lodash';
import { EControlTypes } from '../aux-data/control-types.enum';
import { IFieldBasicData } from '../interfaces/field-basics.interfaces';
import {
  IArraySettings,
  IFormSettings,
  TSpecialFields,
  TSpecialForm,
  TSpecialArray,
} from '../interfaces/form.interfaces';

export class SpecialFormControl<T>
  extends FormControl
  implements IFieldBasicData
{
  public name: string;
  public placeholder: string;
  public label: string;
  public tooltip: string;
  public icon: string;
  public elementId: string;
  public styleClasses: string;
  public length: number;
  public required: boolean;
  public hidden: boolean;
  public readOnly: boolean;
  public settings: T;
  public theme: 'light' | 'dark';
  public type: EControlTypes;
  public errorMessages: { [key: string]: string };

  constructor({
    name,
    settings,
    type,
    validators,
    asyncValidators,
    elementId,
    styleClasses,
    defaultValue,
    hidden,
    icon,
    label,
    length,
    placeholder,
    readOnly,
    required,
    tooltip,
    errorMessages,
  }: TSpecialFields) {
    super(defaultValue, validators, asyncValidators);

    this.name = name;
    this.placeholder = placeholder;
    this.label = label;
    this.tooltip = tooltip;
    this.icon = icon;
    this.elementId = elementId;
    this.settings = settings as T;
    this.type = type;
    this.styleClasses = styleClasses;
    this.hidden = hidden;
    this.length = length;
    this.readOnly = readOnly;
    this.required = required;
    this.errorMessages = errorMessages;
  }

  setReadOnly(status: boolean = true) {
    this.readOnly = status;
  }

  setDisabled(status: boolean = true) {
    status ? this.disable() : this.enable();
  }

  setHidden(status = true) {
    this.hidden = status;
  }
}
export class SpecialFormGroup extends FormGroup implements IFieldBasicData {
  public name: string;
  public placeholder: string;
  public label: string;
  public tooltip: string;
  public icon: string;
  public elementId: string;
  public styleClasses: string;
  public length: number;
  public required: boolean;
  public hidden: boolean;
  public readOnly: boolean;
  public settings: IFormSettings;
  public type: EControlTypes.form;
  public theme: 'light' | 'dark';
  public defaultValue: any;
  public errorMessages: { [key: string]: string };

  constructor(
    {
      name,
      settings,
      type,
      validators,
      asyncValidators,
      elementId,
      styleClasses,
      defaultValue,
      hidden,
      icon,
      label,
      length,
      placeholder,
      readOnly,
      required,
      tooltip,
    }: TSpecialForm,
    controls: {
      [key: string]: SpecialFormControl<any>;
    }
  ) {
    super(controls, validators, asyncValidators);

    this.name = name;
    this.placeholder = placeholder;
    this.label = label;
    this.tooltip = tooltip;
    this.icon = icon;
    this.elementId = elementId;
    this.settings = settings;
    this.type = type;
    this.styleClasses = styleClasses;
    this.hidden = hidden;
    this.length = length;
    this.readOnly = readOnly;
    this.required = required;
    this.defaultValue = defaultValue;
  }

  unpristineRequired() {
    Object.values(this.controls).forEach((control) => {
      if (control instanceof SpecialFormControl) {
        control.markAsDirty();
      } else if (control instanceof SpecialFormGroup) {
        control.unpristineRequired();
      } else if (control instanceof SpecialFormArray) {
        control.unpristineRequired();
      }
    });
  }

